// Функция для установки размера текста путем изменения CSS переменных
function setTextSize(size) {
    let root = document.documentElement;

    if (size === 'small') {
        root.style.setProperty('--tpl-font-size-extra-small', '13px');
        root.style.setProperty('--tpl-font-size-small', '13px');
        root.style.setProperty('--tpl-font-size', '14px');
        root.style.setProperty('--tpl-font-size-plus', '16px');
        root.style.setProperty('--tpl-font-size-medium', '14px');
        root.style.setProperty('--tpl-font-size-medium-plus', '14px');
        root.style.setProperty('--tpl-font-size-large', '28px');
        root.style.setProperty('--tpl-font-size-extra-large', '18px');
        root.style.setProperty('--right-search', '185px');
    } else if (size === 'normal') {
        root.style.setProperty('--tpl-font-size-extra-small', '14px');
        root.style.setProperty('--tpl-font-size-small', '14px');
        root.style.setProperty('--tpl-font-size', '15px');
        root.style.setProperty('--tpl-font-size-plus', '17px');
        root.style.setProperty('--tpl-font-size-medium', '15px');
        root.style.setProperty('--tpl-font-size-medium-plus', '15px');
        root.style.setProperty('--tpl-font-size-large', '30px');
        root.style.setProperty('--tpl-font-size-extra-large', '19px');
        root.style.setProperty('--right-search', '190px');
    } else if (size === 'large') {
        root.style.setProperty('--tpl-font-size-extra-small', '15px');
        root.style.setProperty('--tpl-font-size-small', '15px');
        root.style.setProperty('--tpl-font-size', '16px');
        root.style.setProperty('--tpl-font-size-plus', '18px');
        root.style.setProperty('--tpl-font-size-medium', '16px');
        root.style.setProperty('--tpl-font-size-medium-plus', '16px');
        root.style.setProperty('--tpl-font-size-large', '32px');
        root.style.setProperty('--tpl-font-size-extra-large', '20px');
        root.style.setProperty('--right-search', '195px');
    }

    // Сохраняем выбранный размер текста в cookies
    document.cookie = `textSize=${size}; path=/; max-age=31536000;`; // сохраняем на 1 год
}

// Функция для изменения цвета текста
function setTextColor(color) {
    let root = document.documentElement;

    if (color === 'standart') {
        root.style.setProperty('--link-a', '#93f');
        root.style.setProperty('--link-b', '#A955FF');
        root.style.setProperty('--link-c', '#A955FF');
        root.style.setProperty('--link-a-hover', '#953AF2');
        root.style.setProperty('--link-b-hover', '#A559F2');
        root.style.setProperty('--link-c-hover', '#A559F2');
    } else if (color === 'black') {
        root.style.setProperty('--link-a', '#000');
        root.style.setProperty('--link-b', '#545454');
        root.style.setProperty('--link-c', '#545454');
        root.style.setProperty('--link-a-hover', '#1A1A1A');
        root.style.setProperty('--link-b-hover', '#505050');
        root.style.setProperty('--link-c-hover', '#505050');
    } else if (color === 'blue') {
        root.style.setProperty('--link-a', '#36c');
        root.style.setProperty('--link-b', '#5381DD');
        root.style.setProperty('--link-c', '#5381DD');
        root.style.setProperty('--link-a-hover', '#3866C2');
        root.style.setProperty('--link-b-hover', '#557FD2');
        root.style.setProperty('--link-c-hover', '#557FD2');
    } else if (color === 'green') {
        root.style.setProperty('--link-a', '#1DBA6F');
        root.style.setProperty('--link-b', '#3ED18A');
        root.style.setProperty('--link-c', '#3ED18A');
        root.style.setProperty('--link-a-hover', '#23B16D');
        root.style.setProperty('--link-b-hover', '#42C787');
        root.style.setProperty('--link-c-hover', '#42C787');
    } else if (color === 'red23') {
        root.style.setProperty('--link-a', '#00f');
        root.style.setProperty('--link-b', '#A955FF');
        root.style.setProperty('--link-c', '#A955FF');
        root.style.setProperty('--link-a-hover', '#953AF2');
        root.style.setProperty('--link-b-hover', '#A559F2');
        root.style.setProperty('--link-c-hover', '#A559F2');
    }

    // Сохраняем выбранный цвет текста в cookies
    document.cookie = `textColor=${color}; path=/; max-age=31536000;`; // сохраняем на 1 год
}

// Функция для получения значения cookies
function getCookie(name) {
    let matches = document.cookie.match(new RegExp(
        "(?:^|; )" + name.replace(/([.$?*|{}()\[\]\\\/\+^])/g, '\\$1') + "=([^;]*)"
    ));
    return matches ? decodeURIComponent(matches[1]) : undefined;
}

// Инициализация настройки при загрузке страницы
document.addEventListener('DOMContentLoaded', () => {
    // Применение сохраненного размера текста
    let savedSize = getCookie('textSize');
    if (savedSize) {
        setTextSize(savedSize);
        document.querySelector(`input[name="fontSize"][value="${savedSize}"]`).checked = true;
    }

    // Применение сохраненного цвета текста
    let savedColor = getCookie('textColor');
    if (savedColor) {
        setTextColor(savedColor);
        document.querySelector(`input[name="color"][value="${savedColor}"]`).checked = true;
    }

    // Обработка изменения размера текста
    document.querySelectorAll('input[name="fontSize"]').forEach(input => {
        input.addEventListener('change', () => {
            setTextSize(input.value);
        });
    });

    // Обработка изменения цвета текста
    document.querySelectorAll('input[name="color"]').forEach(input => {
        input.addEventListener('change', () => {
            setTextColor(input.value);
        });
    });
});









